let accordionTriggers = document.querySelectorAll(".js-accordion__trigger"),
    accordionContent = document.querySelectorAll(".js-accordion__content");
for (i = 0; i < accordionTriggers.length; i++) accordionTriggers[i].addEventListener("click", showAccordionContent);

function showAccordionContent(e) {
    let t = document.querySelectorAll(".js-accordion__trigger");
    for (i = 0; i < t.length; i++) t[i].setAttribute("aria-expanded", "false");
    e.target.setAttribute("aria-expanded", "true");
    let o = e.target.getAttribute("aria-controls");
    for (i = 0; i < accordionContent.length; i++) accordionContent[i].style.display = "none";
    document.getElementById(o).style.display = "block"
}
$(document).ready(function () {
    $(".js-toggle").click(function () {
        "+ read more" === $(this).text() ? ($(this).text("â€“ read less"), $(this).prev(".js-more").slideDown()) : ($(this).text("+ read more"), $(this).prev(".js-more").slideUp())
    })
});
